    var docMaskBehaviorPhone = function (val) {
        var shape;
        if (val.replace(/\D/g, '').substr(0, 4).includes('0800')) { shape = '0000 000 0000'; }
        else { shape = val.replace(/\D/g, '').length < 11 ? '(00) 0000-00009' : '(00) 0 0000-0000'; }
        return shape;
    }

